sys_push_notif_msg_content.script:
(function buildJSON(/*GlideRecord*/ current, /*String*/ message, /*Object*/ attributes) {

    var layoutFieldGenerator = new global.NotificationLayoutFieldGenerator();

	//for some reason we need to use getTableName() for this table, instead of the usual sys_class_name
    var identifier = layoutFieldGenerator.layoutField(current.getTableName(), current.sys_id, "task");
    var description = layoutFieldGenerator.layoutField(current.getTableName(), current.sys_id, "task.short_description");
    var status = layoutFieldGenerator.layoutField(current.getTableName(), current.sys_id, "stage");

	var deepLinkGenerator = new global.MobileDeepLinkGenerator("Agent");
	var link = deepLinkGenerator.getFormScreenLink("5a7a52e11bae681065100feddc4bcb97", current.getTableName(), current.getValue("sys_id"));

	var json = {};

	json = {
        "aps" : {
            "sound" : "default"
        },
		"Link": link,
        "Layout" : {
             "Status": status,
             "Identifier" : identifier,
             "Description" : description
        }
    };

    return json;

})(current, message, attributes);
